Go<pre><code>// LoggerMiddleware Log with important fields.
func LoggerMiddleware() gin.HandlerFunc {
    return func(c *gin.Context) {

        startTime := time.Now()
        // Call the remaining handlers for this request
        c.Next()
        stopTime := time.Since(startTime)
        spendTime := fmt.Sprintf("%d ms", int(math.Ceil(float64(stopTime.Nanoseconds()/1000000))))

        statusCode := c.Writer.Status()
        dataSize := c.Writer.Size()
        if dataSize &lt; 0 {
            dataSize = 0
        }
        method := c.Request.Method
        uri := c.Request.RequestURI

        Log := logger.Logger.WithFields(logrus.Fields{
            "spend_time": spendTime,
            "path":       uri,
            "method":     method,
            "status":     statusCode,
        })

        // create inner error
        if len(c.Errors) &gt; 0 {
            Log.Error(c.Errors.ByType(gin.ErrorTypePrivate))
        }
        if statusCode &gt;= errno.Error {
            Log.Error()
        } else if statusCode &gt;= errno.InvalidParams {
            Log.Warn()
        } else {
            Log.Info()
        }

    }
}
</code></pre>

Go<pre><code>package server

import (
    "errors"
    "log"
    "time"
)

const (
    defaultHost    = "127.0.0.1"
    defaultPort    = 8000
    defaultTimeout = time.Minute
    defaultMaxConn = 120
)

type Server struct {
    Host    string
    Port    int
    Timeout time.Duration
    MaxConn int
}

type ServerOptions struct {
    Host    string
    Port    int
    Timeout time.Duration
    MaxConn int
}

type ServerOption interface {
    apply(*ServerOptions)
}

type funcServerOption struct {
    f func(*ServerOptions)
}

func (fo funcServerOption) apply(option *ServerOptions) {
    fo.f(option)
}

func newFuncServerOption(f func(*ServerOptions)) *funcServerOption {
    return &amp;funcServerOption{
        f: f,
    }
}

func WithHost(host string) ServerOption {
    return newFuncServerOption(func(option *ServerOptions) {
        option.Host = host
    })
}

func WithPort(port int) ServerOption {
    return newFuncServerOption(func(option *ServerOptions) {
        option.Port = port
    })
}

func WithTimeout(timeout time.Duration) ServerOption {
    return newFuncServerOption(func(option *ServerOptions) {
        option.Timeout = timeout
    })
}

func WithMaxConn(maxConn int) ServerOption {
    return newFuncServerOption(func(option *ServerOptions) {
        option.MaxConn = maxConn
    })
}

func NewServer(opts ...ServerOption) *Server {
    options := ServerOptions{
        Host:    defaultHost,
        Port:    defaultPort,
        Timeout: defaultTimeout,
        MaxConn: defaultMaxConn,
    }

    for _, opt := range opts {
        opt.apply(&amp;options)
    }

    return &amp;Server{
        Host:    defaultHost,
        Port:    defaultPort,
        Timeout: defaultTimeout,
        MaxConn: defaultMaxConn,
    }
}

func (s *Server) Start() error {
    log.Printf("Server: %v", s)
    return errors.New("Error")
}
</code></pre>

Go<pre><code>package main  

import (  
   "GoSketch/OptionPattern/server"  
   "log"   "time")  

func main() {  
   var opts []server.ServerOption  

   opts = append(opts, server.WithHost("localhost"))  
   opts = append(opts, server.WithPort(8081))  
   opts = append(opts, server.WithTimeout(time.Minute))  
   opts = append(opts, server.WithMaxConn(120))  

   svr := server.NewServer(opts...)  
   if err := svr.Start(); err != nil {  
      log.Fatal(err)  
   }  
}
</code></pre>

